// ../services-definitions/build/index.js
function defineService(id) {
  return id;
}
function implementService(_, factory) {
  return factory;
}
implementService.withConfig = function() {
  return function(_, factory) {
    return factory;
  };
};

// src/core-services/provide-component/definition.ts
var ProvideComponentServiceDefinition = defineService("core:provide-component");

// src/core-services/provide-component/service.ts
import { createElement, Fragment } from "react";
var ProvideComponentServiceFactory = implementService(
  ProvideComponentServiceDefinition,
  ({ config }) => {
    const topComponents = config.topComponents || [];
    const bottomComponents = config.bottomComponents || [];
    const addTopComponent = (Comp) => {
      topComponents.push(Comp);
    };
    const addBottomComponent = (Comp) => {
      bottomComponents.push(Comp);
    };
    const getAllTopComponents = () => () => createElement(
      Fragment,
      null,
      topComponents.map((Comp, index) => createElement(Comp, { key: index }))
    );
    const getAllBottomComponents = () => () => createElement(
      Fragment,
      null,
      bottomComponents.map(
        (Comp, index) => createElement(Comp, { key: index })
      )
    );
    return {
      addTopComponent,
      addBottomComponent,
      getAllTopComponents,
      getAllBottomComponents
    };
  }
);
export {
  ProvideComponentServiceDefinition,
  ProvideComponentServiceFactory
};
